import React from 'react';
import { useState, useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';

import images from '../../constant/images';
import './B'

export const Banner = () => {
    const [loopNum, setLoopNum] = useState(0);
    const [isDeleting, setIsDeleting] = useState(false);
    const toRotate = [ 'UI/UX Developer', 'Front-End Developer', 'Back-End Developer'];
    const [text, setText] = useState('');
    const [delta, setDelta] = useState(300 - Math.random() * 100);
    const period = 2000;

    useEffect (() => {
        let ticker = setInterval(() => {
          tick();
        },delta)
    
        return () => { clearInterval(ticker)};
      }, [text])

      const tick = () => {
        let i = loopNum % toRotate.length;
        let fullText = toRotate[i];
        let updatedText = isDeleting ? fullText.substring(0, text.length - 1) : fullText.substring(0, text.length + 1);
    
        setText(updatedText);
    
        if (isDeleting) {
          setDelta(prevDelta => prevDelta /2)
        }
    
        if (!isDeleting && updatedText === fullText) {
          setIsDeleting(true);
          setDelta(period);
        } else if (isDeleting && updatedText === '') {
          setIsDeleting(false);
          setLoopNum(loopNum + 1);
          setDelta(500);
        }
      }

    return (
        <section className='banner' id='home'>
            <Container>
                <Row className='align-items=center'>
                    <Col xs={12} ms={6} xl={7}>
                        <span className='tagline'>Welcome to my Portfolio</span>
                        <h1>{`Hi I'm David Ozisiaka.`}<span className='wrap'>{text}</span></h1>
                        <p>Ltgfigu jgou uhio UKB  hob ohkbvh uhob abkj uh nkbv bhjh ai vlkjb jnuVV JAHP LJV;ALJV Kjhnkj aihuiobv oya voujhblv ajkjnbnbv zlbv ahh bhv jova i k vab bjam,vnj </p>
                        <button onClick={() => console.log('connect')}>Let's Connect</button>
                    </Col>
    
                    <Col xs={12} md={6} xl={5}>
                        {/* <img  */}
                    </Col>
                </Row>
            </Container>
        </section>
    )  
}
