import React, { useState } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import images from '../../constant/images';
// import emailjs from '@emailjs/browser';

import '../Contact/Contact.css';

export const Contact = () => {

    // const form = useState();

  const formInitialDetails = {
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    message: ''
  };

  const [message, setMessage] = useState('');
  const [isSuccess, setIsSuccess] = useState(false);
  const [formDetails, setFormDetails] = useState(formInitialDetails);
  const [buttonText,] = useState('Send');
//   const [status, setStatus] = useState({});

  const onFormUpdate = (category, value) => {
    setFormDetails({
        ...formDetails,
        [category]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
        const response = await fetch('/api/contact', {
            method: 'POST' ,
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formDetails),
        });

        setMessage('Email sent successfully.');
        setIsSuccess(true);
    } catch (err) {
        setMessage('There was an error sending the email.');
        setIsSuccess(faul)
    }

    // emailjs.sendForm(
    //     'service_6j27vqp', 
    //     'template_48gzjna', 
    //     form.current, 
    //     'kggL4Xu7nIWtqohCb'
    //     )

    //   .then((result) => {
    //       console.log(result.successfully_sent);
    //   }, (error) => {
    //       console.log(error.Error);
    //   });
  };


  return (
    <section className='contact' id='connect'>
        <Container>
            <Row className='align-items-center'>
                <Col md={6}>
                    <img src={images.contactImg} alt='Contact Img' />
                </Col>

                <Col md={6}>
                    <h2>Get In Touch</h2>
                    <form onSubmit={handleSubmit}>
                        <Row>
                            <Col sm={6} className='px-1'>
                                <input type='text' value={formDetails.firstName} placeholder='First Name' onChange={(e) => onFormUpdate('firstName', e.target.value)}></input>
                            </Col>
                            <Col sm={6} className='px-1'>
                                <input type='text' value={formDetails.lastName} placeholder='Last Name' onChange={(e) => onFormUpdate('lastName', e.target.value)}></input>
                            </Col>
                            <Col sm={6} className='px-1'>
                                <input type='email' value={formDetails.email} placeholder='Email' onChange={(e) => onFormUpdate('email', e.target.value)}></input>
                            </Col>
                            <Col sm={6} className='px-1'>
                                <input type='tel' value={formDetails.phone} placeholder='Phone Number' onChange={(e) => onFormUpdate('phone', e.target.value)}></input>
                            </Col>
                            <Col sm={6} className='px-1'>
                                <textarea row={6} value={formDetails.message} placeholder='Message' onChange={(e) => onFormUpdate('message', e.target.value)}></textarea>
                                <button type='submit'><span>{buttonText}</span></button>
                                <div id='error-message' className='error-message'></div>
                            </Col>
                            {/* {
                                status.message && 
                                <Col>
                                    <p className={status.success === false ? 'danger' : 'success'}>{status.message}</p>
                                </Col>
                            } */}
                        </Row>
                    </form>
                </Col>
            </Row>
        </Container>
    </section>
  )



}
